mapper.select:

SELECT 
a.*
, b.seq as CodeGroupSeq
, b.name as CodeGroupName 
FROM code a
LEFT JOIN codegroup b 
    ON b.seq = a.CodeGroupSeq 
        AND a.DelNy = 0
WHERE 1=1

AND a.delNy = #{shDelNy}

    AND a.RegDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
    AND a.ModDateTime BETWEEN #{shDateStart} AND #{shDateEnd}



    AND b.seq = #{shValue}
    AND b.name LIKE CONCAT('%',#{shValue},'%')
    AND a.seq = #{shValue}
    AND a.name LIKE CONCAT('%',#{shValue},'%')


LIMIT #{rowNumToShow} OFFSET #{startRnumForMysql}

mapper.update:
UPDATE code
SET name = #{name}
WHERE 1=1
AND seq = #{seq}

mapper.select:
SELECT COUNT(*) 
FROM codegroup b 
LEFT JOIN code a ON b.seq = a.CodeGroupSeq

    WHERE a.delNY = #{shDelNy}



        AND a.seq = #{shValue}


        AND b.name LIKE CONCAT('%',#{shValue},'%')
