mapper.select:
select a.memberSeq , a.name , a.quitNY , a.dormantNY , a.ID , a.pwd , a.genderCD , a.email , a.birthday , a. MobileNum , a. joinDate , a.modDateTime
from member a
where 1=1 AND a.quitNY = #{shDelNy}
AND a.joinDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.modDatetime BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.memberSeq = #{shValue} AND a.name LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into member (
    name
    , quitNY
    , dormantNY
    , ID
    , pwd
    , genderCD
    , email
    , birthday
    , MobileNum
    , joinDate
    ,modDateTime
) values (
    #{name}
    , #{quitNY}
    , #{dormantNY}
    , #{ID}
    , #{pwd}
    , #{genderCD}
    , #{email}
    , #{birthday}
    , #{MobileNum}
    , NOW()  
    , NOW()
)

mapper.update:
update member
set
	name = #{name}
WHERE 1=1
	and memberSeq = #{memberSeq}

mapper.delete:

delete from member
where 1=1 
and memberSeq=#{memberSeq}

mapper.select:
SELECT COUNT(*) FROM member a 


    WHERE a.quitNY = #{shDelNy}





        AND a.memberSeq = #{shValue}



        AND a.name LIKE CONCAT('%',#{shValue},'%')

