FROM member a
AND a.quitNY = #{shDelNy}
AND a.memberSeq = #{shValue}
AND a.name LIKE CONCAT('%',#{shValue},'%')
LIMIT #{start}, #{pageSize}

mapper.sql:
		FROM member a

				and a.quitNY = 0
