mapper.select:
select a.memberSeq , a.name , a.quitNY , a.dormantNY , a.ID , a.pwd , a.genderCD , a.email , a.birthday , a. MobileNum , a. joinDate , a.modDateTime
from member a
where 1=1 AND a.quitNY = #{shDelNy}
AND a.joinDate BETWEEN #{shDateStart} AND #{shDateEnd} AND a.modDatetime BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.memberSeq = #{shValue} AND a.name LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
insert into member (
    name
    , quitNY
    , dormantNY
    , ID
    , pwd
    , genderCD
    , email
    , birthday
    , MobileNum
    , joinDate
    ,modDateTime
) values (
    #{name}
    , IFNULL(#{quitNY}, 0)
    , IFNULL(#{dormantNY}, 0)
    , #{ID}
    , #{pwd}
    , #{genderCD}
    , #{email}
    , now()
    , #{MobileNum}
    , NOW()  
    , NOW()
)

mapper.delete:

delete from member
where 1=1 
and memberSeq=#{memberSeq}

mapper.select:
SELECT COUNT(*) FROM member a 


    WHERE a.quitNY = #{shDelNy}





        AND a.memberSeq = #{shValue}



        AND a.name LIKE CONCAT('%',#{shValue},'%')

SELECT a.memberSeq , a.name , a.quitNY , a.dormantNY , a.ID , a.pwd , a.genderCD , a.email , a.birthday , a. MobileNum , a. joinDate , a.modDateTime
FROM member a
AND a.quitNY = #{shDelNy}
AND a.memberSeq = #{shValue}
AND a.name LIKE CONCAT('%',#{shValue},'%')
LIMIT #{startRnumForMysql}, #{rowNumToShow}

mapper.sql:
		FROM member a

				and a.quitNY = 0
		where 1=1


		AND a.quitNY = #{shDelNy}


			AND a.joinDate BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.memberSeq = #{shValue}
            AND a.name LIKE CONCAT('%',#{shValue},'%')

mapper.select:
SELECT a.memberSeq, a.name, a.quitNY, a.dormantNY, a.ID, a.pwd, a.genderCD, a.email, a.birthday, a.MobileNum, a.joinDate, a.modDateTime ,b.addrDetail ,b.addr ,b.zipcode ,b.recipient ,b.phoneNum ,b.shippingMsg FROM member a
inner join address b on a.memberSeq=b.Member_seq
WHERE ID = #{ID}
