mapper.insert:
			insert into product (
				a.delNY
				, a.ProductName
				, a.OrgPrice
				, a.DiscountedPrice
				, a.ProductDescription
				, a.prodStockCD
				, a.category1CD
				, a.category2CD
				, b.color
                , c.size

			) values (
			#{delNY}
			, #{ProductName}
			,#{OrgPrice}
			,#{DiscountedPrice}
			,#{ProductDescription}
			,#{prodStockCD}
			,#{category1CD}
			,#{category2CD}
			,#{color}
			,#{size}

mapper.update:
update product
set
	ProductName = #{ProductName}
WHERE 1=1
	and productSeq = #{productSeq}

mapper.select:
SELECT COUNT(*) FROM product a 


    WHERE a.delNY = #{shDelNy}





        AND a.productSeq = #{shValue}



        AND a.ProductName LIKE CONCAT('%',#{shValue},'%')

SELECT a.productSeq , a.delNY , a.ProductName , a.OrgPrice , a.DiscountedPrice , a.ProductDescription , a.prodStockCD , a.category1CD , a.category2CD , a.regDateTime , a.modDateTime , b.color , c.size
from product a inner join prodColor b on a.productSeq=b.Product_productSeq inner join prodSize c on b.Product_productSeq=c.Product_productSeq
AND a.delNY = #{shDelNy}
AND a.productSeq = #{shValue}
AND a.ProductName LIKE CONCAT('%',#{shValue},'%')
LIMIT #{start}, #{pageSize}

mapper.sql:
		FROM product a

				and a.delNY = 0
		where 1=1


		AND a.delNY = #{shDelNy}


			AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.modDateTime BETWEEN #{shDateStart} AND #{shDateEnd}


		limit 99999999999
		) aa
		limit 5 offset 0


            AND a.productSeq = #{shValue}
            AND a.ProductName LIKE CONCAT('%',#{shValue},'%')
